	def menuEditSettingsOnMenuSelection( self, event ):
		app = wx.App.Get()
		with SettingsDialogImp(self, app.data_directory) as dlg:
			if dlg.ShowModal() == wx.ID_OK:
				if dlg.dirty:
					# more than this needs to happen
					app.data_directory = dlg.data_directory

